        boxShadow: 'none',
        color: '#453318',
        '&:hover': {
            border: '5px solid #FCE0BE',
            backgroundColor: '#FCE0BE'
        },
        '&:active': {
            boxShadow: 'none',
            backgroundColor: '#FCE0BE',
            borderColor: '#FCE0BE',
    }},
    avatarHello: {
        // Position
        position: 'absolute',
        left: 'calc(-100% - 80px - 28px)',
        top: 'calc(50% - 24px)',
