        borderRadius: '12px',
        border: '2px solid #FCE0BE',
    },
    bottomText: {
        position: 'absolute',
        width: '100%',
        top: '60%',
        color: '#FCE0BE',
        fontSize: '24px',
        fontWeight: '600'
    },
    loadingDiv: {
        position: 'absolute',
        height: '100%',
        width: '100%',
